export function getLightningService(transaction: ITransaction): ILightningService | null {
  if (
    transaction.identifiedService === "tippin" ||
    transaction.identifiedService === "lngames" ||
    transaction.identifiedService === "zigzag" ||
    transaction.identifiedService === "satoshisplace" ||
    transaction.identifiedService === "lnbig"
  ) {
    return null;
  }

  if (transaction.identifiedService && transaction.identifiedService in lightningServices) {
    return lightningServices[transaction.identifiedService];
  }
  return null;
}